INSERT INTO `feedback` (`id`, `title`, `content`, `fullname`, `email`, `date`, `is_read`, `is_answered`) VALUES
(9, 'Reservation of boat ticket', 'Hello, I would like to reserve a boat ticket with the Superdong 3 on 4 April \nLeaving Rach Gia at 08:00 a.m / arrival Phu Quoc at 10:15 a.m.\n\nIs there a place where I could buy the ticket directly in Saigon? Or could I reserve the ticket with you and pick it up in Rach Gia or in Saigon?\nWith best regards, Birgit Hable', 'Birgit Hable', 'birgit.hable@live.de', 1333122903, 1, NULL),
(5, 'tours', 'e muon dat tour, nhung e o tp.HCM vay di nhu the nao de tiet kiem nhat? neu e k di 1 so dia diem: nha lao PQ,.... e chi di tau ra dao, lan ngam san ho, cau muc, trai nuoi cay trai, cho dem PQ,noi nuoi thu hoach to yen thi chi phi co tiet kiem hon K? neu di 4-5 nguoi thi chi phi nhu the nao? e du dinh di khoang 10/3/2011.\nPS: ct anh co stock, tham du nhu the nao?', 'khanh Nguyen', 'comet124a@yahoo.com', 1299339947, 1, NULL),
(6, 'Chào Mừng Bạn Đến Với Hits.VN801', 'Giới Thiệu\n1 - Hệ thống surf nhanh chỉ với 5s là bạn đã nhận được Credits\n2 - Ðăng ký thành viên dễ dàng,nhanh chóng,và nhận ngay 1000 Credits\n3 - Kích hoạt tài khoản và website đều do thành viên đãng ký tự thực hiện,không phải chờ đợi lâu\n4 - Bạn có thể add 10 website & URL vào tài khoản và phân phối credits cho các URL\n---------------------------------------------------------------------------------\nQuy Ðịnh Cơ Bản\n1 - Tất cả Free member đều phải Surf 10 trang web để kích hoạt tài khoản\n2 - Không được add các website có chứa Virus, Phá khung surf, xxx, và liên quan đến chính trị, tôn giáo, kỳ thị chủng tộc\n3 - Không add các website, banner không hoạt động, có Pop-up\n4 - Bạn phải thực hiện surf thường xuyên để kiếm credits,nếu tài khoản không thực hiện surf trong vòng 1 tuần sẽ bị delete URL & Website của tài khoản đó. Tuy nhiên bạn có thể add Website lại khi bạn tham gia surf tiếp.\n---------------------------------------------------------------------------------\nNguyên Tắc Hoạt Ðộng\nNguyên tắc hoạt động của Hits.VN801.Com thật đõn giản. Bạn xem các website của các Thành viên khác trong Hits.VN801.Com và ngược lại các thành viên đó sẽ xem Website của bạn. Hàng vạn thành viên xem Website của nhau cũng có nghĩa là có rất nhiều người truy cập vào website của bạn. (Truy cập này là bắt buộc và hoàn toàn tự động) Chính vì vậy hiệu suất cũng như hiệu quả đạt được rất cao.\n---------------------------------------------------------------------------------\nWELCOME TO HITS.VN801.COM - HIT.VN801.COM', 'ghjgjh', 'ntdctt29560@gmail.com', 1311602612, 1, NULL),
(8, 'Boat to Ha Tein', 'I was wondering if you could give me the times, prices and options with regards to getting to Ha Tien by boat. Do you know if you can get a visa for Cambodia at that border crossing? Thanks!', 'Leslie', 'Lkerscht@mymts.net', 1329269188, NULL, NULL),
(10, 'Gop y Ws', 'Sao Ws ko có thông tin gì hết vậy, sơ xài quá', 'Rio Nguyen', 'quantam265@yahoo.com', 1337589880, NULL, NULL);

