--
-- Table structure for table `news_rate`
--

CREATE TABLE IF NOT EXISTS `news_rate` (
  `id` int(11) NOT NULL auto_increment,
  `type` tinyint(1) default NULL,
  `value` float default NULL,
  `date` int(11) default NULL,
  `news_id` int(11) default NULL,
  `users_id` int(11) NOT NULL default '0',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `news_tags`
--

CREATE TABLE IF NOT EXISTS `news_tags` (
  `id` int(11) NOT NULL,
  `is_module` tinyint(1) NOT NULL default '0',
  `is_enabled` tinyint(1) NOT NULL default '0',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- --------------------------------------------------------

--
-- Table structure for table `news_tags_lang`
--

CREATE TABLE IF NOT EXISTS `news_tags_lang` (
  `tags_id` int(11) NOT NULL,
  `lang` varchar(3) collate utf8_unicode_ci NOT NULL default 'vn',
  `name` text collate utf8_unicode_ci NOT NULL,
  `url` varchar(64) collate utf8_unicode_ci NOT NULL,
  `description` text collate utf8_unicode_ci NOT NULL,
  `img_symbol` varchar(100) collate utf8_unicode_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- --------------------------------------------------------

--
-- Table structure for table `news_tags_news`
--

CREATE TABLE IF NOT EXISTS `news_tags_news` (
  `tags_id` int(11) NOT NULL,
