INSERT INTO `tour_lang` (`tour_id`, `lang`, `title`, `url`, `summary`, `content`, `time_tour`, `price`, `vehicle`, `destination`) VALUES
(9, 'vi', 'ĐẢO XANH PHÚ QUỐC      (Tour 4 Ngày 3 Đêm)', NULL, '<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">Đến với Ph&uacute; Quốc, Du kh&aacute;ch sẽ trải nghiệm những gi&acirc;y ph&uacute;t thư gi&atilde;n với cảnh ho&agrave;ng h&ocirc;n tuyệt đẹp, huyền ảo, th&uacute; vị. Du Lịch H&agrave;nh Tr&igrave;nh Xanh đưa du kh&aacute;ch đến với những b&atilde;i biển c&aacute;t trắng trải d&agrave;i,  h&ograve;a m&igrave;nh v&agrave;o d&ograve;ng nước trong xanh, ngắm nh&igrave;n những tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n  dưới đ&aacute;y đại dương với những rặng san h&ocirc; rực rỡ đầy m&agrave;u sắc. Kh&aacute;m ph&aacute; những kỳ quan thi&ecirc;n nhi&ecirc;n thơ mộng...<br /></span></p>', '<div>\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="color: #0000ff; font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: small; color: #ffffff;">Ngày 1 : PHÚ QU&Ocirc;́C &ndash; BI&Ecirc;̉N XANH C&Aacute;T TRẮNG</span><br /></span></div>\n<div style="text-align: justify;"><span style="background-color: #ffffff; font-size: xx-small; font-family: tahoma,arial,helvetica,sans-serif;">.</span></div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">Xe   v&agrave; HDV Cty đ&oacute;n đo&agrave;n tại điểm thống nhất, đưa đo&agrave;n về nh&agrave; h&agrave;ng d&ugrave;ng cơm   trưa, sau đ&oacute; xe đưa đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</span></div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;"><br /></span></div>\n<div>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><span style="color: #0ca4f2;"><strong>14h00:</strong></span><span style="color: #000000;"> Xe đưa đo&agrave;n đến b&atilde;i Sao một trong những b&atilde;i biển đẹp nhất tại Ph&uacute; Quốc.   Tr&ecirc;n đường đi qu&yacute; kh&aacute;ch được nghe giới thiệu về c&aacute;c địa danh nổi tiếng  ở  đ&acirc;y v&agrave; c&ugrave;ng tham gia c&aacute;c tr&ograve; chơi vận động tr&ecirc;n xe do hướng dẫn vi&ecirc;n   phụ tr&aacute;ch. Đến b&atilde;i Sao qu&yacute; kh&aacute;ch sẽ đắm m&igrave;nh trong d&ograve;ng nước trong  xanh  v&agrave; chi&ecirc;m ngưỡng b&atilde;i c&aacute;t trắng uốn cong như h&igrave;nh thiếu nữ vươn m&igrave;nh  ra  biển. Qu&yacute; kh&aacute;ch thưởng thức c&aacute;c m&oacute;n hải sản tươi sống, thuần t&uacute;y  tại Ph&uacute;  Quốc như: Cơm Ghẹ, gỏi c&aacute; tr&iacute;ch...(<em>chi ph&iacute; tự t&uacute;c).</em></span></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><span style="color: #000000;"><em><br /></em></span></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #0ca4f2;"><strong>16h00:</strong></span> <span style="color: #000000;">Xe  đưa đo&agrave;n tham quan L&agrave;ng Ch&agrave;i H&agrave;m Ninh một trong những l&agrave;ng ch&agrave;i cổ   nhất tại Việt Nam, đo&agrave;n sẽ tham quan v&agrave; mua sắm đặc sản tươi sống tại   đ&acirc;y như: Hải M&atilde;, Hải Long, T&ocirc;m T&iacute;ch, Ghẹ....</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #000000;"><br /></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #0ca4f2;"><strong>17h00:</strong></span> <span style="color: #000000;">Xe đưa đo&agrave;n về nh&agrave; h&agrave;ng d&ugrave;ng cơm chiều. Sau đ&oacute; về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi, tự do kh&aacute;m ph&aacute; Ph&uacute; Quốc về đ&ecirc;m (<em>thưởng thức hải sản tại chợ đ&ecirc;m Ph&uacute; Quốc)</em></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><em><br /></em></span></p>\n</div>\n</div>\n</div>\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #ffffff;">Ngày 2: PHÚ QU&Ocirc;́C &ndash; THAM QUAN NAM ĐẢO ( C&acirc;u c&aacute; &amp; Lặn ngắm San h&ocirc;)</span></div>\n<div>&nbsp;</div>\n<div>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #0ca4f2;"><strong>07H30:</strong> </span><span style="color: #000000;">Xe  v&agrave; HDV đ&oacute;n đo&agrave;n tại điểm đ&atilde; thống nhất. Sau đ&oacute; khởi h&agrave;nh đi tham  quan  Nam Đảo. Tr&ecirc;n đường đi đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại nh&agrave; h&agrave;ng. Sau đ&oacute;  xe  đưa đo&agrave;n đến tham quan trại nu&ocirc;i cấy Ngọc Trai, nơi sản xuất những  vi&ecirc;n  ngọc tự nhi&ecirc;n. Qu&yacute; kh&aacute;ch sẽ được nghe c&aacute;ch nu&ocirc;i cấy v&agrave; lấy ngọc.  Qu&yacute;  kh&aacute;ch c&oacute; thể mua về l&agrave;m qu&agrave;.</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #000000;"><br /></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #0ca4f2;"><strong>09h00: </strong></span><span style="color: #000000;">Đo&agrave;n   tiếp tục h&agrave;nh tr&igrave;nh xe đưa đo&agrave;n gh&eacute; lại quầy h&agrave;ng kh&ocirc; để qu&yacute; kh&aacute;ch mua   c&aacute;c m&oacute;n kh&ocirc; về l&agrave;m qu&agrave; cho người th&acirc;n. Tiếp tục h&agrave;nh tr&igrave;nh Đến cảng An   Thới HDV cty sẽ đưa qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u tham quan quần đảo An Thới với  c&aacute;c  đảo nhỏ: H&ograve;n Dừa - H&ograve;n R&otilde;i &ndash; H&ograve;n Thơm. Đo&agrave;n sẽ được chi&ecirc;m ngưỡng  những  b&atilde;i biển c&aacute;t trắng v&agrave; d&ograve;ng nước xanh trong. T&agrave;u sẽ dừng lại ngo&agrave;i  đảo ở  đ&acirc;y qu&yacute; kh&aacute;ch sẽ trổ t&agrave;i bắt c&aacute; bằng dụng cụ c&acirc;u c&aacute; chuy&ecirc;n  nghiệp của  ngư d&acirc;n tr&ecirc;n đảo. D&ugrave;ng cơm trưa tr&ecirc;n t&agrave;u với c&aacute;c chiến lợi  phẩm thu  được.</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">T&agrave;u   cập v&agrave;o gần bờ, du kh&aacute;ch sẽ được trang bị đồ bơi, vật dụng cần thiết  để  h&ograve;a m&igrave;nh v&agrave;o d&ograve;ng nước trong xanh, ngắm nh&igrave;n những tuyệt t&aacute;c thi&ecirc;n   nhi&ecirc;n dưới đ&aacute;y đại dương với những rặng san h&ocirc; rực rỡ đầy m&agrave;u sắc, c&ugrave;ng   những đ&agrave;n c&aacute; tung tăng bơi lội.<strong>&nbsp;&nbsp;</strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #0ca4f2;"><strong>16H00: </strong></span><span style="color: #000000;">Xe   đưa đo&agrave;n tham quan di t&iacute;ch lịch sử Nh&agrave; lao C&acirc;y Dừa - nơi giam cầm   khoảng 40 ng&agrave;n t&ugrave; binh ch&iacute;nh trị của chế độ h&agrave; khắc thời Ph&aacute;p thuộc. Sau   đ&oacute; xe đưa đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng nghĩ ngơi. Tư do kh&aacute;m ph&aacute; Ph&uacute;   Quốc về đ&ecirc;m.</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #000000;"><br /></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><strong>Chương tr&igrave;nh đi k&egrave;m: </strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><em><span style="color: #0ca4f2;"><strong>17H00:</strong></span> <span style="color: #000000;">xe  v&agrave; HDV đưa qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u v&agrave; ra khơi. Ở đ&acirc;y qu&yacute; kh&aacute;ch sẽ trải   nghiệm những gi&acirc;y ph&uacute;t thư gi&atilde;n với&nbsp; cảnh ho&agrave;ng h&ocirc;n tuyệt đẹp v&agrave; huyền   ảo đầy th&uacute; vị, Qu&yacute; kh&aacute;ch c&ugrave;ng thủy thủ đo&agrave;n trổ t&agrave;i c&acirc;u mực. Sẽ kh&ocirc;ng g&igrave;   bằng khi ch&iacute;nh tay bạn c&acirc;u được những con mực đang săn mồi dưới biển   hay vớt được những ch&uacute; c&aacute; kiếm, c&aacute; xanh xương đang nổi m&igrave;nh tr&ecirc;n mặt   biển v&agrave; c&ugrave;ng trải nghiệm cảm gi&aacute;c ra khơi như c&aacute;c ngư d&acirc;n tr&ecirc;n đảo.   Chiến lợi phẩm sẽ được phục vụ ngay tr&ecirc;n t&agrave;u.</span></em></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><em><span style="color: #000000;"><br /></span></em></span></p>\n</div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #0ca4f2;"><strong><em>21H00:</em></strong></span><span style="color: #000000;"><em> kết th&uacute;c chuyến c&acirc;u mực đ&ecirc;m, xe đưa du kh&aacute;ch về kh&aacute;ch sạn nghỉ ngơi.<strong>Chi ph&iacute; tự t&uacute;c (300.000/người). Chương tr&igrave;nh sẽ do qu&yacute; kh&aacute;ch ấn định thời điểm</strong></em></span></span></div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #000000;"><em><strong><br /></strong></em></span></span></div>\n<div style="text-align: justify;">\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #ffffff;">Ngày 3: PH&Uacute; QUỐC - KH&Aacute;M PH&Aacute; THI&Ecirc;N NHI&Ecirc;N<br /></span></div>\n<div>&nbsp;</div>\n</div>\n</div>\n<div style="text-align: justify;">\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #0ca4f2;"><strong>&nbsp;</strong><strong>7h30: </strong></span><span style="color: #000000;">Qu&yacute; kh&aacute;ch ăn s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute; xe đưa qu&yacute; kh&aacute;ch tham quan vườn ti&ecirc;u Khu Tượng, nơi sản xuất những hạt ti&ecirc;u ngon, thơm nồng lừng danh khắp cả nước, một trong những mặt h&agrave;ng xuất khẩu chủ đạo của Ph&uacute; Quốc. Sau đ&oacute; xe đưa qu&yacute; kh&aacute;ch tham quan Suối Đ&aacute; B&agrave;n, một ngọn suối cao, rộng kho&aacute;c l&ecirc;n m&igrave;nh những chiếc b&agrave;n lớn khổng lồ tạo th&agrave;nh bởi đ&aacute; tự nhi&ecirc;n với nhiều hồ nước rộng, qu&yacute; kh&aacute;ch c&oacute; thể ho&agrave; m&igrave;nh trong d&ograve;ng nước m&aacute;t b&ecirc;n khung cảnh thi&ecirc;n nhi&ecirc;n thơ mộng.</span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #000000;"><br /></span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #0ca4f2;"><strong>11h30:</strong></span> <span style="color: #000000;">Xe đưa đo&agrave;n về nh&agrave; h&agrave;ng d&ugrave;ng cơm trưa. Sau đ&oacute; về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #000000;"><br /></span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #0ca4f2;"><strong>14h00:</strong></span> <span style="color: #000000;">Xe đưa đo&agrave;n tham quan Cội nguồn nơi bảo tồn nhiều lo&agrave;i sinh vật qu&yacute; hiếm của Ph&uacute; Quốc v&agrave; l&agrave; nơi trưng b&agrave;y n&eacute;t văn h&oacute;a của Ph&uacute; Quốc, nơi đ&acirc;y c&ograve;n bảo tồn 1 giống ch&oacute; qu&yacute; hiếm của thế giới m&agrave; hầu như ai cũng muốn chi&ecirc;m ngưỡng lo&agrave;i vật tinh kh&ocirc;n n&agrave;y.</span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #000000;"><br /></span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #0ca4f2;"><strong>15h00:</strong></span> <span style="color: #000000;">Xe đưa đo&agrave;n tham quan L&agrave;ng Ch&agrave;i H&agrave;m Ninh một trong những l&agrave;ng ch&agrave;i cổ nhất tại Việt Nam, đo&agrave;n sẽ tham quan v&agrave; mua sắm đặc sản tươi sống tại đ&acirc;y như: Hải M&atilde;, Hải Long, T&ocirc;m T&iacute;ch, Ghẹ....Sau đ&oacute; xe đưa kh&aacute;ch về tham quan l&ograve; sản xuất rượu Sim Bảy Gi&aacute;o người m&agrave; đ&atilde; th&agrave;nh c&ocirc;ng trong việc chiết xuất những giọt rượu bằng nguy&ecirc;n liệu l&agrave; những tr&aacute;i Sim. Sau đ&oacute; xe đưa qu&yacute; kh&aacute;ch tham quan nh&agrave; th&ugrave;ng nơi sản xuất ra những giọt nước mắm thơm nồng đ&atilde; tạo n&ecirc;n danh tiếng cho h&ograve;n đảo thơ mộng vốn c&oacute; cảnh quan thi&ecirc;n nhi&ecirc;n đẹp v&ocirc; thường.</span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #000000;"><br /></span></span></p>\n<p><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif;"><span style="color: #0ca4f2;"><strong>17h00:</strong></span> <span style="color: #000000;">Xe đưa qu&yacute; kh&aacute;ch về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi. Qu&yacute; kh&aacute;ch tự do dạo phố, kh&aacute;m ph&aacute; Ph&uacute; Quốc về đ&ecirc;m</span></span></p>\n</div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #0000ff;"><em><strong><br /></strong></em></span></div>\n<div>\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #ffffff;">Ngày 4: TIỄN KH&Aacute;CH RA S&Acirc;N BAY PH&Uacute; QUỐC<br /></span></div>\n<div>&nbsp;</div>\n</div>\n<div><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: small; color: #0000ff;">&nbsp;</span><span style="font-size: small; line-height: 115%; color: #000000;">Đo&agrave;n   d&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn, tự do ngắm cảnh b&igrave;nh minh Ph&uacute; Quốc.   Xe đưa qu&yacute; kh&aacute;ch ra Bến t&agrave;u hoặc s&acirc;n bay. Kết th&uacute;c chuyến tham quan,   Chia tay v&agrave; hẹn gặp lại</span></span></div>\n<div><span style="font-size: small; line-height: 115%; color: #0000ff; font-family: tahoma,arial,helvetica,sans-serif;"><br /></span></div>\n<div>\n<p style="text-align: justify;"><span style="text-decoration: underline; color: #ff0000; font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: small;"><strong>Gi&aacute; tr&ecirc;n bao gồm:</strong></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Kh&aacute;ch sạn đạt ti&ecirc;u chuẩn 2 sao, 3 sao, 4 sao, tiện nghi đầy đủ (2-3 kh&aacute;ch/ph&ograve;ng)</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Ăn uống: C&aacute;c bữa ăn được chọn lọc đảm bảo vệ sinh an to&agrave;n thực phẩm. 06 bữa ăn ch&iacute;nh, 03 bữa ăn s&aacute;ng.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Khăn lạnh, nước uống (02 chai 0.5l/kh&aacute;ch/ng&agrave;y)</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Xe tham quan du lịch đời mới, m&aacute;y lạnh, phục vụ ri&ecirc;ng từng nh&oacute;m suốt h&agrave;nh tr&igrave;nh. Kh&ocirc;ng gh&eacute;p với nh&oacute;m kh&aacute;c.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Du thuyền tham quan tr&ecirc;n biển phục vụ ri&ecirc;ng cho từng nh&oacute;m từ 6 kh&aacute;ch trở l&ecirc;n.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; VAT 10%</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;"> <strong>PHỤC VỤ:</strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Hướng dẫn vi&ecirc;n chu đ&aacute;o, vui vẻ, nhiệt t&igrave;nh suốt tuyến, biết về nghiệp vụ sơ cấp cứu.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Thuốc y tế th&ocirc;ng thường: say xe, đau bụng, băng gạt, dầu gi&oacute;,&hellip;</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Tham quan theo chương tr&igrave;nh đ&iacute;nh k&egrave;m.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;"> <strong>QU&Agrave; TẶNG C&Ocirc;NG TY: </strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Một tấm ảnh lưu niệm.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; N&oacute;n du lịch</span></p>\n<p style="text-align: justify;"><span style="font-family: tahoma,arial,helvetica,sans-serif;"><span style="font-size: small;"> <strong><span style="text-decoration: underline;">Gi&aacute; tour kh&ocirc;ng bao gồm</span></strong></span><span style="font-size: small;"><strong>:</strong></span><span style="font-size: small; color: #000000;"><strong><br /></strong></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Ăn uống, điện thoại, c&aacute;c dịch vụ vui chơi giải tr&iacute; c&aacute; nh&acirc;n ngo&agrave;i chương tr&igrave;nh.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Phương tiện vận chuyển: xe, t&agrave;u, m&aacute;y bay.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; C&acirc;u mực đ&ecirc;m.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #ff0000;"><strong><em><span style="text-decoration: underline;">Ghi ch&uacute;:</span></em></strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em từ 11 tuổi trở l&ecirc;n: t&iacute;nh bằng gi&aacute; người lớn.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em từ 06 đến dưới 11 tuổi: t&iacute;nh &frac12; gi&aacute; v&eacute;, bao gồm: 1 suất ăn, 1 chỗ ngồi tr&ecirc;n xe, v&eacute; tham quan, ngủ chung với gia đ&igrave;nh.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em dưới 06 tuổi: miễn ph&iacute; (2 người lớn chỉ k&egrave;m 1 trẻ miễn ph&iacute;, trẻ thứ 2 phải mua 30% gi&aacute; tour).</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: tahoma,arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp;  Trẻ em mua V&eacute; m&aacute;y bay theo qui định của Việt Nam Airline, Air Mekong  (tr&ecirc;n 12 tuổi mua 100% v&eacute;, từ 02 - dưới 12 tuổi mua 75% v&eacute;, dưới 02 tuổi  mua 10%v&eacute;).</span></p>\n</div>\n</div>\n</div>', '4 ngày 3 đêm', '3.852.800 VND / 1 người', 'Máy bay', 'Phú Quốc'),
(8, 'vi', 'PHÚ QUỐC - NẮNG VÀ BIỂN                         (Tour 3 Ngày 2 Đêm)', NULL, '<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">Đến với Ph&uacute; Quốc, du kh&aacute;ch sẽ được chi&ecirc;m ngưỡng những b&atilde;i biển c&aacute;t trắng, h&ograve;a m&igrave;nh v&agrave;o d&ograve;ng nước trong xanh, ngắm nh&igrave;n những tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n dưới đ&aacute;y đại dương với những rặng san h&ocirc; rực rỡ đầy m&agrave;u sắc. C&ugrave;ng trải nghiệm những gi&acirc;y ph&uacute;t thư gi&atilde;n với cảnh ho&agrave;ng h&ocirc;n tuyệt đẹp v&agrave; huyền ảo đầy th&uacute; vị...</span></p>', '<div>\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="color: #0000ff;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #ffffff;">Ngày 1 : PHÚ QU&Ocirc;́C &ndash; BI&Ecirc;̉N XANH C&Aacute;T TRẮNG</span><br /></span></div>\n<div style="text-align: justify;"><span style="background-color: #ffffff; font-size: xx-small;">.</span></div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">Xe  v&agrave; HDV Cty đ&oacute;n đo&agrave;n tại điểm thống nhất, đưa đo&agrave;n về nh&agrave; h&agrave;ng d&ugrave;ng cơm  trưa, sau đ&oacute; xe đưa đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</span></div>\n<div>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><strong>14h00:</strong><span style="color: #000000;"> Xe đưa đo&agrave;n đến b&atilde;i Sao một trong những b&atilde;i biển đẹp nhất tại Ph&uacute; Quốc.  Tr&ecirc;n đường đi qu&yacute; kh&aacute;ch được nghe giới thiệu về c&aacute;c địa danh nổi tiếng ở  đ&acirc;y v&agrave; c&ugrave;ng tham gia c&aacute;c tr&ograve; chơi vận động tr&ecirc;n xe do hướng dẫn vi&ecirc;n  phụ tr&aacute;ch. Đến b&atilde;i Sao qu&yacute; kh&aacute;ch sẽ đắm m&igrave;nh trong d&ograve;ng nước trong xanh  v&agrave; chi&ecirc;m ngưỡng b&atilde;i c&aacute;t trắng uốn cong như h&igrave;nh thiếu nữ vươn m&igrave;nh ra  biển. Qu&yacute; kh&aacute;ch thưởng thức c&aacute;c m&oacute;n hải sản tươi sống, thuần t&uacute;y tại Ph&uacute;  Quốc như: Cơm Ghẹ, gỏi c&aacute; tr&iacute;ch...(<em>chi ph&iacute; tự t&uacute;c).</em></span></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><strong>16h00:</strong></span> <span style="color: #000000;">Xe đưa đo&agrave;n tham quan L&agrave;ng Ch&agrave;i H&agrave;m Ninh một trong những l&agrave;ng ch&agrave;i cổ  nhất tại Việt Nam, đo&agrave;n sẽ tham quan v&agrave; mua sắm đặc sản tươi sống tại  đ&acirc;y như: Hải M&atilde;, Hải Long, T&ocirc;m T&iacute;ch, Ghẹ....</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #ff0000;"><strong><span style="color: #008000;">17h00:</span></strong></span> <span style="color: #000000;">Xe đưa đo&agrave;n về nh&agrave; h&agrave;ng d&ugrave;ng cơm chiều. Sau đ&oacute; về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi, tự do kh&aacute;m ph&aacute; Ph&uacute; Quốc về đ&ecirc;m (<em>thưởng thức hải sản tại chợ đ&ecirc;m Ph&uacute; Quốc)</em></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><em><br /></em></span></p>\n</div>\n</div>\n</div>\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #ffffff;">Ngày 2: PHÚ QU&Ocirc;́C &ndash; THAM QUAN NAM ĐẢO ( C&acirc;u c&aacute; &amp; Lặn ngắm San h&ocirc;)</span></div>\n<div>&nbsp;</div>\n<div>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><strong>07H30:</strong> </span><span style="color: #000000;">Xe v&agrave; HDV đ&oacute;n đo&agrave;n tại điểm đ&atilde; thống nhất. Sau đ&oacute; khởi h&agrave;nh đi tham  quan Nam Đảo. Tr&ecirc;n đường đi đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại nh&agrave; h&agrave;ng. Sau đ&oacute;  xe đưa đo&agrave;n đến tham quan trại nu&ocirc;i cấy Ngọc Trai, nơi sản xuất những  vi&ecirc;n ngọc tự nhi&ecirc;n. Qu&yacute; kh&aacute;ch sẽ được nghe c&aacute;ch nu&ocirc;i cấy v&agrave; lấy ngọc.  Qu&yacute; kh&aacute;ch c&oacute; thể mua về l&agrave;m qu&agrave;.</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><strong>09h00: </strong></span><span style="color: #000000;">Đo&agrave;n  tiếp tục h&agrave;nh tr&igrave;nh xe đưa đo&agrave;n gh&eacute; lại quầy h&agrave;ng kh&ocirc; để qu&yacute; kh&aacute;ch mua  c&aacute;c m&oacute;n kh&ocirc; về l&agrave;m qu&agrave; cho người th&acirc;n. Tiếp tục h&agrave;nh tr&igrave;nh Đến cảng An  Thới HDV cty sẽ đưa qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u tham quan quần đảo An Thới với c&aacute;c  đảo nhỏ: H&ograve;n Dừa - H&ograve;n R&otilde;i &ndash; H&ograve;n Thơm. Đo&agrave;n sẽ được chi&ecirc;m ngưỡng những  b&atilde;i biển c&aacute;t trắng v&agrave; d&ograve;ng nước xanh trong. T&agrave;u sẽ dừng lại ngo&agrave;i đảo ở  đ&acirc;y qu&yacute; kh&aacute;ch sẽ trổ t&agrave;i bắt c&aacute; bằng dụng cụ c&acirc;u c&aacute; chuy&ecirc;n nghiệp của  ngư d&acirc;n tr&ecirc;n đảo. D&ugrave;ng cơm trưa tr&ecirc;n t&agrave;u với c&aacute;c chiến lợi phẩm thu  được.</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">T&agrave;u  cập v&agrave;o gần bờ, du kh&aacute;ch sẽ được trang bị đồ bơi, vật dụng cần thiết để  h&ograve;a m&igrave;nh v&agrave;o d&ograve;ng nước trong xanh, ngắm nh&igrave;n những tuyệt t&aacute;c thi&ecirc;n  nhi&ecirc;n dưới đ&aacute;y đại dương với những rặng san h&ocirc; rực rỡ đầy m&agrave;u sắc, c&ugrave;ng  những đ&agrave;n c&aacute; tung tăng bơi lội.<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><strong>16H00: </strong></span><span style="color: #000000;">Xe  đưa đo&agrave;n tham quan di t&iacute;ch lịch sử Nh&agrave; lao C&acirc;y Dừa - nơi giam cầm  khoảng 40 ng&agrave;n t&ugrave; binh ch&iacute;nh trị của chế độ h&agrave; khắc thời Ph&aacute;p thuộc. Sau  đ&oacute; xe đưa đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng nghĩ ngơi. Tư do kh&aacute;m ph&aacute; Ph&uacute;  Quốc về đ&ecirc;m.</span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><strong>Chương tr&igrave;nh đi k&egrave;m: </strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><em><span style="color: #008000;"><strong>17H00:</strong></span> <span style="color: #000000;">xe v&agrave; HDV đưa qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u v&agrave; ra khơi. Ở đ&acirc;y qu&yacute; kh&aacute;ch sẽ trải  nghiệm những gi&acirc;y ph&uacute;t thư gi&atilde;n với&nbsp; cảnh ho&agrave;ng h&ocirc;n tuyệt đẹp v&agrave; huyền  ảo đầy th&uacute; vị, Qu&yacute; kh&aacute;ch c&ugrave;ng thủy thủ đo&agrave;n trổ t&agrave;i c&acirc;u mực. Sẽ kh&ocirc;ng g&igrave;  bằng khi ch&iacute;nh tay bạn c&acirc;u được những con mực đang săn mồi dưới biển  hay vớt được những ch&uacute; c&aacute; kiếm, c&aacute; xanh xương đang nổi m&igrave;nh tr&ecirc;n mặt  biển v&agrave; c&ugrave;ng trải nghiệm cảm gi&aacute;c ra khơi như c&aacute;c ngư d&acirc;n tr&ecirc;n đảo.  Chiến lợi phẩm sẽ được phục vụ ngay tr&ecirc;n t&agrave;u.</span></em></span></p>\n</div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><span style="color: #008000;"><strong><em>21H00:</em></strong></span><span style="color: #000000;"><em> kết th&uacute;c chuyến c&acirc;u mực đ&ecirc;m, xe đưa du kh&aacute;ch về kh&aacute;ch sạn nghỉ ngơi.<strong>Chi ph&iacute; tự t&uacute;c (300.000/người). Chương tr&igrave;nh sẽ do qu&yacute; kh&aacute;ch ấn định thời điểm</strong></em></span></span></div>\n<div style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;"><em><strong><br /></strong></em></span></div>\n<div>\n<div>\n<div style="background-color: #009900; height: 16px; padding: 3px 5px; color: #ffffff; font-weight: bold;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #ffffff;">Ngày 3: TIỄN KH&Aacute;CH RA S&Acirc;N BAY PH&Uacute; QUỐC<br /></span></div>\n<div>&nbsp;</div>\n</div>\n<div><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #0000ff;">&nbsp;</span><span style="font-size: small; line-height: 115%; color: #000000; font-family: arial,helvetica,sans-serif;">Đo&agrave;n  d&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn, tự do ngắm cảnh b&igrave;nh minh Ph&uacute; Quốc.  Xe đưa qu&yacute; kh&aacute;ch ra Bến t&agrave;u hoặc s&acirc;n bay. Kết th&uacute;c chuyến tham quan,  Chia tay v&agrave; hẹn gặp lại</span></div>\n<div><span style="font-size: small; line-height: 115%; color: #0000ff; font-family: arial,helvetica,sans-serif;"><br /></span></div>\n<div>\n<p style="text-align: justify;"><span style="text-decoration: underline; color: #008000;"><span style="font-family: arial,helvetica,sans-serif; font-size: small;"><strong>Gi&aacute; tr&ecirc;n bao gồm:</strong></span></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Kh&aacute;ch sạn đạt ti&ecirc;u chuẩn 2 sao, 3 sao, 4 sao, tiện nghi đầy đủ (2-3 kh&aacute;ch/ph&ograve;ng)</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Ăn uống: C&aacute;c bữa ăn được chọn lọc đảm bảo vệ sinh an to&agrave;n thực phẩm. 04 bữa ăn ch&iacute;nh, 02 bữa ăn s&aacute;ng.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Khăn lạnh, nước uống (02 chai 0.5l/kh&aacute;ch/ng&agrave;y)</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Xe tham quan du lịch đời mới, m&aacute;y lạnh, phục vụ ri&ecirc;ng từng nh&oacute;m suốt h&agrave;nh tr&igrave;nh. Kh&ocirc;ng gh&eacute;p với nh&oacute;m kh&aacute;c.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Du thuyền tham quan tr&ecirc;n biển phục vụ ri&ecirc;ng cho từng nh&oacute;m từ 6 kh&aacute;ch trở l&ecirc;n.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; VAT 10%</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;"> <strong>PHỤC VỤ:</strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Hướng dẫn vi&ecirc;n chu đ&aacute;o, vui vẻ, nhiệt t&igrave;nh suốt tuyến, biết về nghiệp vụ sơ cấp cứu.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Thuốc y tế th&ocirc;ng thường: say xe, đau bụng, băng gạt, dầu gi&oacute;,&hellip;</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Tham quan theo chương tr&igrave;nh đ&iacute;nh k&egrave;m.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;"> <strong>QU&Agrave; TẶNG C&Ocirc;NG TY: </strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; Một tấm ảnh lưu niệm.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">*&nbsp;&nbsp; N&oacute;n du lịch</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif;"> <strong><span style="text-decoration: underline;">Gi&aacute; tour kh&ocirc;ng bao gồm</span></strong></span><span style="font-size: small; font-family: arial,helvetica,sans-serif;"><strong>:</strong></span><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;"><strong><br /></strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Ăn uống, điện thoại, c&aacute;c dịch vụ vui chơi giải tr&iacute; c&aacute; nh&acirc;n ngo&agrave;i chương tr&igrave;nh.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Phương tiện vận chuyển: xe, t&agrave;u, m&aacute;y bay.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; C&acirc;u mực đ&ecirc;m.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #ff0000;"><strong><em><span style="text-decoration: underline;">Ghi ch&uacute;:</span></em></strong></span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em từ 11 tuổi trở l&ecirc;n: t&iacute;nh bằng gi&aacute; người lớn.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em từ 06 đến dưới 11 tuổi: t&iacute;nh &frac12; gi&aacute; v&eacute;, bao gồm: 1 suất ăn, 1 chỗ ngồi tr&ecirc;n xe, v&eacute; tham quan, ngủ chung với gia đ&igrave;nh.</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em dưới 06 tuổi: miễn ph&iacute; (2 người lớn chỉ k&egrave;m 1 trẻ miễn ph&iacute;, trẻ thứ 2 phải mua 30% gi&aacute; tour).</span></p>\n<p style="text-align: justify;"><span style="font-size: small; font-family: arial,helvetica,sans-serif; color: #000000;">-&nbsp;&nbsp; Trẻ em mua V&eacute; m&aacute;y bay theo qui định của Việt Nam Airline, Air Mekong (tr&ecirc;n 12 tuổi mua 100% v&eacute;, từ 02 - dưới 12 tuổi mua 75% v&eacute;, dưới 02 tuổi mua 10%v&eacute;).</span></p>\n</div>\n</div>\n</div>', '3 ngày 2 đêm', '2.753.800 VND / 1 người', 'Máy bay', 'Phú Quốc'),
